import{_ as n,c as r,d as l,e as i,o as e,r as o}from"./app.dde4153b.js";const m=JSON.parse('{"title":"Linux 快捷键","description":"Linux 命令行常用快捷键介绍, ctrl + k,ctrl + u, ctrl + w.","frontmatter":{"title":"Linux 快捷键","head":[["meta",{"property":"og:title","content":"Linux 快捷键"}],["meta",{"name":"description","content":"Linux 命令行常用快捷键介绍, ctrl + k,ctrl + u, ctrl + w."}],["meta",{"name":"keywords","content":"Linux,快捷键,ctrl + k,ctrl + u, ctrl + w"}],["meta",{"property":"og:description","content":"Linux 命令行常用快捷键介绍, ctrl + k,ctrl + u, ctrl + w."}],["meta",{"property":"og:url","content":"https://wiki.sanoon.me/vientiane/Linux%E5%BF%AB%E6%8D%B7%E9%94%AE"}],["link",{"rel":"canonical","href":"https://wiki.sanoon.me/vientiane/Linux%E5%BF%AB%E6%8D%B7%E9%94%AE"}]]},"headers":[{"level":2,"title":"历史命令快捷键","slug":"历史命令快捷键","link":"#历史命令快捷键","children":[]},{"level":2,"title":"命令行快捷键","slug":"命令行快捷键","link":"#命令行快捷键","children":[]},{"level":2,"title":"后台快捷键","slug":"后台快捷键","link":"#后台快捷键","children":[]},{"level":2,"title":"清屏快捷键","slug":"清屏快捷键","link":"#清屏快捷键","children":[]},{"level":2,"title":"快速切换目录","slug":"快速切换目录","link":"#快速切换目录","children":[]}],"relativePath":"vientiane/Linux快捷键.md"}'),s={name:"vientiane/Linux快捷键.md"},a=i('<h1 id="linux-命令行常用快捷键" tabindex="-1">Linux 命令行常用快捷键 <a class="header-anchor" href="#linux-命令行常用快捷键" aria-hidden="true" loading="lazy">#</a></h1><h2 id="历史命令快捷键" tabindex="-1">历史命令快捷键 <a class="header-anchor" href="#历史命令快捷键" aria-hidden="true" loading="lazy">#</a></h2><ul><li><strong>ctrl + p</strong>, 上一条命令</li><li><strong>ctrl + n</strong>, 下一条命令</li><li><strong>ctrl + r</strong>, 搜索历史命令, 结果翻页也是使用该组合键</li><li><strong>ctrl + g</strong>, 从 <strong>ctrl + r</strong> 中退出</li></ul><h2 id="命令行快捷键" tabindex="-1">命令行快捷键 <a class="header-anchor" href="#命令行快捷键" aria-hidden="true" loading="lazy">#</a></h2><ul><li><strong>tab</strong>, 命令补全</li><li><strong>ctrl + a</strong>, 移动到行首</li><li><strong>ctrl + e</strong>, 移动到行尾</li><li><strong>ctrl + b</strong>, 从光标位置后退</li><li><strong>ctrl + f</strong>, 从光标位置前进</li><li><strong>ctrl + h</strong>, 删除光标的前一个字符</li><li><strong>ctrl + d</strong>, 删除当前光标所在位置的字符</li><li><strong>ctrl + k</strong>, 删除光标之后的所有字符</li><li><strong>ctrl + u</strong>, 删除光标之前的所有字符</li><li><strong>ctrl + w</strong>, 删除光标签的单词</li><li><strong>ctrl + y</strong>, 回退 <strong>ctrl + k|w|u</strong> 删除的内容</li><li><strong>ctrl + c</strong>, 终止当前任务的执行</li><li><strong>!!</strong>, 执行上一条命令</li><li><strong>!num</strong>, 执行历史命令的第 <strong>num</strong> 条命令</li><li><strong>!$</strong>, 代指上一条命令的最后一个参数，该命令常用于shell脚本中</li><li><strong>esc + b</strong>, 移动到当前单词的开头</li><li><strong>esc + f</strong>, 移动到当前单词的结尾</li></ul><h2 id="后台快捷键" tabindex="-1">后台快捷键 <a class="header-anchor" href="#后台快捷键" aria-hidden="true" loading="lazy">#</a></h2><ul><li><strong>ctrl + z</strong>, 将当前进程放入后台, 例如针对 <strong>tail -f file</strong> 命令</li><li><strong>fg</strong>, 显示 <strong>ctrl + z</strong> 后台执行的命令</li></ul><h2 id="清屏快捷键" tabindex="-1">清屏快捷键 <a class="header-anchor" href="#清屏快捷键" aria-hidden="true" loading="lazy">#</a></h2><ul><li><strong>ctrl + l</strong>, 快捷清屏, 类似 <strong>clear</strong> 命令</li></ul><h2 id="快速切换目录" tabindex="-1">快速切换目录 <a class="header-anchor" href="#快速切换目录" aria-hidden="true" loading="lazy">#</a></h2><ul><li><strong>cd -</strong>, 快速回到上一次的工作目录</li><li><strong>cd ~</strong> 或者 <strong>cd</strong>, 快速回到用户目录</li></ul><hr>',12);function c(g,d,h,u,_,p){const t=o("GoogleAdsense");return e(),r("div",null,[a,l(t)])}const f=n(s,[["render",c]]);export{m as __pageData,f as default};
